mapper.select:
#         select * from course where mybatis.school.schoolname = #{schoolname};
        select CourseId,name,hours,sid from mybatis.course, mybatis.school where
            sid=school.id and school.schoolname=#{schoolname};

mapper.update:
update mybatis.course
set name=#{name},hours=#{hours},sid=#{sid}
where CourseId=#{CourseId};
